const { chromium } = require('playwright');

(async () => {
  const browser = await chromium.connectOverCDP('http://localhost:9222');
  const context = browser.contexts()[0] || await browser.newContext();
  const page = await context.newPage();

  console.log("=== FETCHING MARKET DATA FROM YAHOO FINANCE ===\n");

  try {
    await page.goto('https://finance.yahoo.com/', { timeout: 30000 });
    await page.waitForTimeout(3000);

    // Get market summary
    const marketData = await page.evaluate(() => {
      const results = {};
      // Look for index tickers
      const items = document.querySelectorAll('[data-testid="quote-container"], [data-field="regularMarketPrice"]');
      items.forEach(item => {
        const text = item.textContent;
        results[text.substring(0, 50)] = true;
      });
      return document.body.innerText.substring(0, 5000);
    });

    console.log("Yahoo Finance content sample:");
    console.log(marketData);

  } catch (e) {
    console.log("Yahoo Error:", e.message);
  }

  console.log("\n\n=== FETCHING NEWS FROM REUTERS ===\n");

  try {
    await page.goto('https://www.reuters.com/world/us/', { timeout: 30000 });
    await page.waitForTimeout(3000);

    const newsText = await page.evaluate(() => {
      return document.body.innerText.substring(0, 8000);
    });

    console.log("Reuters US News:");
    console.log(newsText);

  } catch (e) {
    console.log("Reuters Error:", e.message);
  }

  await browser.close();
})();

